function satpDiff = diffSatp(satpEph, satpAlm)    satpDiff = satpAlm;  % If there is disabled satellite?  col = 1;  colEph = 1;  while col != 32    if(col != satpEph(1,colEph))      satpDiff(1:end,col) = [col;NaN;NaN;NaN];      col = col+1;      continue;    endif    satpDiff(2:end,col) = satpEph(2:end,colEph) - satpAlm(2:end,col);    col = col+1;    colEph = colEph+1;  end            end  